import "./App.css";
import { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [datas, setDatas] = useState([]);
  const [userId, setUserId] = useState(""); // 추가
  const [password, setPassword] = useState(""); // 추가
  const [message, setMessage] = useState(""); // 추가

  const getData = async () => {
    try {
      const response = await axios.get("/data");
      console.log(response.data);
      setDatas(response.data);
    } catch (err) {}
  };

  // ============ 여기부터 추가 ============
  const handleLogin = async () => {
    try {
      const response = await axios.post("/login", {
        user_id: userId,
        password: password,
      });
      console.log(response.data);
      setMessage(response.data.message);
    } catch (err) {
      console.error(err);
    }
  };
  // ============ 여기까지 추가 ============

  useEffect(() => {
    getData();
  }, []);

  return (
    <div className="App">
      <div className="container">
        <h1 className="title">TO DO LIST</h1>
        <p>{JSON.stringify(datas)}</p>
        {message && <p style={{ color: "red" }}>{message}</p>} {/* 추가 */}
        <div className="login">
          <div className="input">
            <label>아이디</label>
            <input
              type="text"
              placeholder="아이디를 입력하세요"
              value={userId} // 추가
              onChange={(e) => setUserId(e.target.value)} // 추가
            />
          </div>

          <div className="input">
            <label>비밀번호</label>
            <input
              type="password"
              placeholder="비밀번호를 입력하세요"
              value={password} // 추가
              onChange={(e) => setPassword(e.target.value)} // 추가
            />
          </div>

          <button className="login-btn" onClick={handleLogin}>
            {" "}
            {/* 수정 */}
            로그인
          </button>

          <button className="signup-btn">회원가입</button>
        </div>
      </div>
    </div>
  );
}

export default App;
