import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Main() {
  const [todos, setTodos] = useState([]);
  const [title, setTitle] = useState("");
  const [list, setList] = useState("");
  const [editId, setEditId] = useState(null);
  const [editTitle, setEditTitle] = useState("");
  const [editList, setEditList] = useState("");
  const navigate = useNavigate();

  // Todo 목록 조회
  const getTodos = async () => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/");
      return;
    }

    try {
      const response = await axios.get("/todos", {
        headers: { authorization: token }
      });
      setTodos(response.data);
    } catch (err) {
      console.error(err);
    }
  };

  // Todo 생성
  const handleCreate = async () => {
    if (!title) {
      alert("제목을 입력하세요");
      return;
    }

    const token = localStorage.getItem("token");
    try {
      await axios.post(
        "/todos",
        { title, list },
        { headers: { authorization: token } }
      );
      setTitle("");
      setList("");
      getTodos();
    } catch (err) {
      console.error(err);
    }
  };

  // Todo 수정
  const handleUpdate = async (id) => {
    const token = localStorage.getItem("token");
    const todo = todos.find(t => t.todo_id === id);
    
    try {
      await axios.put(
        `/todos/${id}`,
        { 
          title: editTitle, 
          list: editList,
          completed: todo.completed 
        },
        { headers: { authorization: token } }
      );
      setEditId(null);
      getTodos();
    } catch (err) {
      console.error(err);
    }
  };

  // Todo 삭제
  const handleDelete = async (id) => {
    const token = localStorage.getItem("token");
    try {
      await axios.delete(`/todos/${id}`, {
        headers: { authorization: token }
      });
      getTodos();
    } catch (err) {
      console.error(err);
    }
  };

  // 완료 상태 변경
  const handleToggle = async (id) => {
    const token = localStorage.getItem("token");
    const todo = todos.find(t => t.todo_id === id);
    
    try {
      await axios.put(
        `/todos/${id}`,
        { 
          title: todo.title, 
          list: todo.list,
          completed: !todo.completed 
        },
        { headers: { authorization: token } }
      );
      getTodos();
    } catch (err) {
      console.error(err);
    }
  };

  // 로그아웃
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  useEffect(() => {
    getTodos();
  }, []);

  return (
    <div className="App">
      <div className="container">
        <h1 className="title">TO DO LIST</h1>

        <button className="signup-btn" onClick={handleLogout}>
          로그아웃
        </button>

        {/* Todo 생성 */}
        <div className="login">
          <div className="input">
            <label>제목</label>
            <input
              type="text"
              placeholder="제목을 입력하세요"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </div>
          <div className="input">
            <label>내용</label>
            <input
              type="text"
              placeholder="내용을 입력하세요 (선택)"
              value={list}
              onChange={(e) => setList(e.target.value)}
            />
          </div>
          <button className="login-btn" onClick={handleCreate}>
            추가
          </button>
        </div>

        {/* Todo 목록 */}
        <div style={{ marginTop: "30px" }}>
          {todos.map((todo) => (
            <div
              key={todo.todo_id}
              style={{
                background: "white",
                padding: "15px",
                marginBottom: "10px",
                borderRadius: "4px",
                border: "1px solid #ddd"
              }}
            >
              {editId === todo.todo_id ? (
                // 수정 모드
                <>
                  <input
                    type="text"
                    value={editTitle}
                    onChange={(e) => setEditTitle(e.target.value)}
                    style={{ width: "100%", padding: "5px", marginBottom: "5px" }}
                  />
                  <input
                    type="text"
                    value={editList}
                    onChange={(e) => setEditList(e.target.value)}
                    style={{ width: "100%", padding: "5px", marginBottom: "10px" }}
                  />
                  <button
                    onClick={() => handleUpdate(todo.todo_id)}
                    style={{ marginRight: "5px" }}
                  >
                    저장
                  </button>
                  <button onClick={() => setEditId(null)}>취소</button>
                </>
              ) : (
                // 일반 모드
                <>
                  <h3
                    style={{
                      textDecoration: todo.completed ? "line-through" : "none"
                    }}
                  >
                    {todo.title}
                  </h3>
                  <p>{todo.list}</p>
                  <button
                    onClick={() => handleToggle(todo.todo_id)}
                    style={{ marginRight: "5px" }}
                  >
                    {todo.completed ? "미완료" : "완료"}
                  </button>
                  <button
                    onClick={() => {
                      setEditId(todo.todo_id);
                      setEditTitle(todo.title);
                      setEditList(todo.list);
                    }}
                    style={{ marginRight: "5px" }}
                  >
                    수정
                  </button>
                  <button onClick={() => handleDelete(todo.todo_id)}>
                    삭제
                  </button>
                </>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Main;
