import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleLogin = async () => {
    // üîπ ÌîÑÎ°†Ìä∏ ÏµúÏÜå Í≤ÄÏ¶ù
    if (!email || !password) {
      setMessage("Ïù¥Î©îÏùºÍ≥º ÎπÑÎ∞ÄÎ≤àÌò∏Î•º Î™®Îëê ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      return;
    }
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      setMessage("Ïò¨Î∞îÎ•∏ Ïù¥Î©îÏùº ÌòïÏãùÏù¥ ÏïÑÎãôÎãàÎã§.");
      return;
    }

    try {
      const response = await axios.post("/login", {
        email: email,
        password: password,
      });

      localStorage.setItem("token", response.data.token);
      setMessage(response.data.message);
    } catch (err) {
      if (err.response) {
        setMessage(err.response.data.message);
      } else {
        setMessage("ÏÑúÎ≤Ñ Ïó∞Í≤∞ Ïã§Ìå®");
      }
    }
  };

  return (
    <div className="App">
      <div className="container">
        <h1 className="title">TO DO LIST</h1>

        {message && <p className="message">{message}</p>}

        <div className="login">
          <div className="input">
            <label>Ïù¥Î©îÏùº</label>
            <input
              type="email"
              placeholder="Ïù¥Î©îÏùºÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>

          <div className="input">
            <label>ÎπÑÎ∞ÄÎ≤àÌò∏</label>
            <input
              type="password"
              placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>

          <button className="login-btn" onClick={handleLogin}>
            Î°úÍ∑∏Ïù∏
          </button>

          <button className="signup-btn" onClick={() => navigate("/signup")}>
            ÌöåÏõêÍ∞ÄÏûÖ
          </button>
        </div>
      </div>
    </div>
  );
}

export default Login;
